mapper.select:
select CODE_NAME as codeName
from public_code_tb 
where GROUP_CODE_NO = #{groupCodeNo} 
	  and CODE_NO = #{codeNo}

select USER_UID as userUid
from chat_user_info_tb 
where CHAT_ROOM_INFO_NO = #{no1};

mapper.delete:
delete 
from chat_user_info_tb 
where USER_UID = #{userUid} 
  and CHAT_ROOM_INFO_NO = #{chatRoomInfoNo}
